import React, { useEffect, useMemo, useState } from "react";
import "./header.css";
import { useQuery, useMutation } from "@apollo/client";
import {
  LOGIN_WITH_GOOGLE,
  LOGIN_WITH_GOOGLE_MUTATION,
  EXISTING_LOGIN,
} from "../../gql/query";

import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import Menu from "@mui/material/Menu";
import MenuIcon from "@mui/icons-material/Menu";
import Container from "@mui/material/Container";
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import Tooltip from "@mui/material/Tooltip";
import MenuItem from "@mui/material/MenuItem";
import GoogleIcon from "@mui/icons-material/Google";
// const pages = ["Products", "Pricing", "Blog"];
const settings = ["Logout"];

function Header({ setIsLoggedIn, isLoggedIn, setLoading }) {
  const [accessToken, setAccessToken] = useState();
  const { data, loading: loginDataLoading } = useQuery(LOGIN_WITH_GOOGLE);
  const [loginMutation, { data: mutationData, loading: mutationDataLoading }] =
    useMutation(LOGIN_WITH_GOOGLE_MUTATION);
  const [authForUser, { data: authData, loading: authLoading }] =
    useMutation(EXISTING_LOGIN);

  useEffect(() => {
    const urlCode = new URLSearchParams(window.location.search).get("code");
    if (urlCode) {
      loginMutation({ variables: { code: urlCode, service: "GOOGLE" } });
    }
    setAccessToken(mutationData?.loginForLogin?.token);
  }, []);

  // useEffect(() => {
  //   if (!mutationData.loginForLogin.user && accessToken) {
  //     authForUser({ variables: { token: accessToken } });
  //   }
  // }, []);
  // console.log("authData ", authData);
  if (accessToken) localStorage.setItem("_id", accessToken);
  // if(isLoggedIn)
  const loggedInData = useMemo(() =>
    setIsLoggedIn(mutationData?.loginForLogin?.user || undefined)
  );
  console.log("This, ", isLoggedIn);
  const [anchorElNav, setAnchorElNav] = React.useState(null);
  const [anchorElUser, setAnchorElUser] = React.useState(null);

  const handleOpenNavMenu = (event) => {
    setAnchorElNav(event.currentTarget);
  };
  const handleOpenUserMenu = (event) => {
    setAnchorElUser(event.currentTarget);
  };

  const handleCloseNavMenu = () => {
    setAnchorElNav(null);
  };

  const handleCloseUserMenu = () => {
    setAnchorElUser(null);
  };

  const onLogout = () => {
    localStorage.removeItem("_id");
  };
  console.log(!!localStorage.getItem("_id"));
  return (
    <AppBar position="static">
      <Container maxWidth="xl">
        <Toolbar disableGutters sx={{ justifyContent: "space-between" }}>
          <Typography
            variant="h6"
            noWrap
            component="div"
            sx={{ mr: 2, display: { xs: "none", md: "flex" } }}
          >
            Marketplace
          </Typography>

          <Box sx={{ flexGrow: 1, display: { xs: "flex", md: "none" } }}>
            <IconButton
              size="large"
              aria-label="account of current user"
              aria-controls="menu-appbar"
              aria-haspopup="true"
              onClick={handleOpenNavMenu}
              color="inherit"
            >
              <MenuIcon />
            </IconButton>
          </Box>

          <Box sx={{ flexGrow: 0 }}>
            <Tooltip title="Login">
              {!isLoggedIn && !localStorage.getItem("_id") ? (
                <a href={data?.getLoginUrlsForLogin?.GOOGLE}>
                  <Button variant="contained" startIcon={<GoogleIcon />}>
                    <span sx={{ textDecoration: "none" }}>Login</span>
                  </Button>
                </a>
              ) : (
                <IconButton onClick={handleOpenUserMenu} sx={{ p: 0 }}>
                  {isLoggedIn.avatar.url ? (
                    <Avatar alt="Remy Sharp" src={isLoggedIn.avatar.url} />
                  ) : (
                    <Avatar src="/broken-image.jpg" />
                  )}
                </IconButton>
              )}
            </Tooltip>
            <Menu
              sx={{ mt: "45px" }}
              id="menu-appbar"
              anchorEl={anchorElUser}
              anchorOrigin={{
                vertical: "top",
                horizontal: "right",
              }}
              keepMounted
              transformOrigin={{
                vertical: "top",
                horizontal: "right",
              }}
              open={Boolean(anchorElUser)}
              onClose={handleCloseUserMenu}
            >
              <MenuItem onClick={handleCloseNavMenu}>
                <Typography textAlign="center">
                  <a
                    onClick={onLogout}
                    href={`https://www.google.com/accounts/Logout?continue=https://appengine.google.com/_ah/logout?continue=${window.origin}`}
                  >
                    Logout
                  </a>
                </Typography>
              </MenuItem>
            </Menu>
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
}

export default Header;
