import { React, useState } from "react";
import "./Product.css";
import { useStateValue } from "../stateProvider/StateProvider";
import { Elements } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";
function Product({ id, title, price, rating, image }) {
  const stripePromise = loadStripe(
    "pk_test_51K8TyeSA4k3N3EwFkev9itWmqjgBb3JKGzm10zPbtBZD6JYlIE35d7Dgq6EHXnBl39XvnddS86oUV4v8lRI72Vev00LGYgw6WA"
  );
  const [{}, dispatch] = useStateValue();
  const [loading, setLoading] = useState();
  const addToBasket = () => {
    //Add item to basket...
    dispatch({
      type: "ADD_TO_BASKET",
      item: {
        id,
        title,
        image,
        price,
        rating,
      },
    });
  };
  const handleClick = async () => {
    setLoading(true);
    const stripe = await stripePromise;

    const { error } = await stripe.redirectToCheckout({
      lineItem: [{ price: "price_1K8hBASA4k3N3EwFN5SHAe4g", quantity: 1 }],
      mode: "payment",
      cancelUrl: "localhost:3000/",
      successUrl: "localhost:3000/home",
    });
    if (error) {
      setLoading(false);
      console.log(error, "error");
      //   setStripeError(error);
    }
  };
  const options = {
    // passing the client secret obtained from the server
    clientSecret:
      "sk_test_51K8TyeSA4k3N3EwFW612M8fGHikJ6Gm4XCXt7xFmEH8F361mjHBSH1lB8nLpxgmr9CP1MRSYuuSTsTRliHX9cGiw00AC45VCPm",
  };
  return (
    <Elements stripe={stripePromise} options={options}>
      <div className="product">
        <div className="product__info">
          <p>{title}</p>
          <p className="product__price">
            <small>$</small>
            <strong>{price}</strong>
          </p>
          <div className="product__rating">
            {Array(rating)
              .fill()
              .map((index) => (
                <i key={index} class="fa fa-star"></i>
              ))}
          </div>
        </div>
        <img src={image} alt="" />
        <button onClick={handleClick}>Add to basket</button>
      </div>
    </Elements>
  );
}

export default Product;
