import React, { useState } from "react";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";

import { loadStripe } from "@stripe/stripe-js";
function Product({ id, title, price, rating, image, price_api, isLoggedIn }) {
  const stripePromise = loadStripe(
    "pk_test_51K8TyeSA4k3N3EwFkev9itWmqjgBb3JKGzm10zPbtBZD6JYlIE35d7Dgq6EHXnBl39XvnddS86oUV4v8lRI72Vev00LGYgw6WA"
  );
  const [loading, setLoading] = useState();
  const handleClick = async () => {
    if (isLoggedIn) {
      setLoading(true);
      const stripe = await stripePromise;
      const { error } = await stripe.redirectToCheckout({
        lineItems: [
          {
            price: price_api,
            quantity: 1,
          },
        ],
        mode: "payment",
        cancelUrl: "http://localhost:3000",
        successUrl: "http://localhost:3000/home",
      });
      if (error) {
        setLoading(false);
        console.log("The error ", error);
      }
    } else {
      alert("Please log in");
    }
  };

  return (
    <Card
      sx={{
        maxHeight: 450,
        minWidth: 100,
        width: "25%",
        margin: "3rem",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
        alignItems: "center",
      }}
    >
      <CardMedia
        component="img"
        alt="title"
        height="auto"
        image={image}
        sx={{ objectFit: "contain", maxHeight: "200px" }}
      />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          {title}
        </Typography>
      </CardContent>
      <CardActions>
        <Button
          variant="contained"
          size="small"
          onClick={handleClick}
          disabled={loading}
        >
          Buy now
        </Button>
      </CardActions>
    </Card>
  );
}

export default Product;
