import React, { useEffect, useMemo, useState } from "react";
import { useQuery } from "@apollo/client";

import "./Home.css";
import Product from "../product/Product.js";
import { GET_PRODUCTS } from "../../gql/query";
import { Box, CircularProgress } from "@mui/material";
function Home({ isLoggedIn }) {
  const { data, loading: productsLoading } = useQuery(GET_PRODUCTS);

  // productsLoading ? setLoading(true) : setLoading(false);

  const products = useMemo(() => data?.products || []);
  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "row",
        flexWrap: "wrap",
        gap: "4rem",
        marginTop: "4rem",
      }}
    >
      {productsLoading && (
        <CircularProgress sx={{ position: "absolute", left: "50%" }} />
      )}
      {products.map((item, i) => {
        return (
          <Product
            key={i}
            id={item.id}
            title={item.title}
            image={item.image.url}
            price={item.price}
            rating={item.ratings}
            price_api={item.priceApiId}
            isLoggedIn={isLoggedIn} //Checking if the user is logged in
          />
        );
      })}
    </Box>
  );
}

export default Home;
