import React, { useEffect, useMemo, useState } from "react";
import "./Home.css";
import Product from "../product/Product.js";
import { GET_PRODUCTS } from "../../gql/query";
import { useQuery } from "@apollo/client";

function Home({ loading, setLoading, isLoggedIn }) {
  console.log(loading);
  const { data, loading: productsLoading } = useQuery(GET_PRODUCTS);

  // productsLoading ? setLoading(true) : setLoading(false);

  useEffect(() => {
    setLoading(productsLoading);
  }, [loading, productsLoading, data]);
  const products = useMemo(() => data?.products || []);
  return (
    <div className="home">
      {/*product id, title, price, rating */}

      <div className="home__row">
        {products.map((item) => {
          return (
            <Product
              id={item.id}
              title={item.title}
              image={item.image.url}
              price={item.price}
              rating={item.ratings}
              price_api={item.priceApiId}
              isLoggedIn={isLoggedIn}
            />
          );
        })}
      </div>
    </div>
  );
}

export default Home;
